mapper.select:
SELECT t1.id, t1.user_id, t1.work_url, t1.work_head_img, t1.work_desc , t1.work_type, t2.user_name, t1.work_status,t1.created_time, (select count(1) from user_enroll_work_event where work_id = t1.id and event_type =1) as work_pv, (select count(1) from user_enroll_work_event where work_id = t1.id and event_type =2) as work_like, (select count(1) from user_enroll_work_event where work_id = t1.id and event_type =3) as work_vote, t1.created_time FROM user_enroll_work t1 INNER JOIN custom_user t2 ON t1.user_id = t2.id WHERE 1=1
AND t1.work_type = #{type}
AND t1.work_status = #{state}
order by work_vote desc
order by t1.created_time desc
AND t1.work_status = #{state}
order by t1.created_time desc

SELECT cu.id, user_name, user_number, user_city, user_country , IFNULL(uew.work_status,30) AS work_status, (2-(SELECT COUNT(1) FROM user_enroll_work_event uewe WHERE uewe.send_user_id = cu.id AND uewe.event_type = 3)) AS vote_count,ifnull(t1.score,0) as score, (select count(1) from user_prize_record where user_id = cu.id and prize_type = 1) as redpack_count, (select count(1) from user_prize_record where user_id = cu.id and prize_type = 2) as coupon_count FROM custom_user cu LEFT JOIN (select sum(score) as score,user_id from user_score_record group by user_id) as t1 ON cu.id = t1.user_id LEFT JOIN user_enroll_work uew ON cu.id = uew.user_id AND uew.work_type = 1
AND user_name = #{queryParams.userName}
AND user_number = #{queryParams.userNumber}
AND user_city = #{queryParams.userCity}
AND login_state = #{queryParams.state}
order by cu.created_time asc

select t1.id as prize_id,t2.id as
user_id,t2.user_name,t2.user_number,t1.prize_type,t1.prize_name,t1.created_time
from user_prize_record t1
inner join custom_user t2
on t1.user_id = t2.id


        AND t1.prize_type = #{prizeType}


        AND prize_name = #{prizeName}


        AND t2.user_name = #{userName}


        AND t2.user_number = #{userNumber}


order by t1.created_time desc

select t2.id,t2.user_name,t2.user_number,t1.score,t1.score_channel,t1.created_time from user_score_record t1 inner join custom_user t2 on t1.user_id = t2.id
AND t2.user_name = #{userName}
AND t2.user_number = #{userNumber}
order by t1.created_time desc

select t1.id,t2.id as
user_id,t2.user_name,t2.user_number,t2.user_city,t1.draw_type,t1.draw_round,t1.created_time
from user_draw_record t1
inner join custom_user t2
on t1.user_id = t2.id


        AND t1.draw_type = #{drawType}


        AND t1.draw_round = #{round}


        AND t2.user_name = #{userName}


        AND t2.user_number = #{userNumber}


order by t1.created_time desc
