mapper.select:
SELECT t1.id, t1.user_id, t1.work_url, t1.work_head_img, t1.work_desc , t1.work_type, t2.user_name, t1.created_time, (select count(1) from user_enroll_work_event where work_id = t1.id and event_type =1) as work_pv, (select count(1) from user_enroll_work_event where work_id = t1.id and event_type =2) as work_like, (select count(1) from user_enroll_work_event where work_id = t1.id and event_type =3) as work_vote FROM user_enroll_work t1 INNER JOIN custom_user t2 ON t1.user_id = t2.id WHERE t1.work_type = #{type}
order by work_vote desc
order by t1.created_time desc
